function toggleLang(){var c=document.getElementsByClassName("desc_it");var a=document.getElementsByClassName("desc_en");var d=document.getElementsByClassName("lang_select");stateIT=c[0].style.display;for(var b=0;b<c.length;b++){if(stateIT=="none"){d[b].className="lang_select en";c[b].style.display="block";a[b].style.display="none"}else{d[b].className="lang_select it";c[b].style.display="none";a[b].style.display="block "}}};