function toggleLang(){var c=document.getElementsByClassName("desc_it");var a=document.getElementsByClassName("desc_en");lan_sel=document.getElementById("lang_select");stateIT=c[0].style.display;for(var b=0;b<c.length;b++){if(stateIT=="none"){c[b].style.display="block";a[b].style.display="none"}else{c[b].style.display="none";a[b].style.display="block "}}if(stateIT=="none"){lan_sel.style.backgroundImage="url(../img/flaguk.png)"}else{lan_sel.style.backgroundImage="url(../img/flagit.png)"}};